/// <reference types="node" />

export type Method = 'xml' | 'xmlmin' | 'json' | 'jsonmin' | 'css' | 'cssone' | 'cssmin' | 'sql' | 'sqlmin' | 'js' | 'jsmin' | 'html' | 'htmlmin'

/**
 * option 参数
 *
 * tabsize 和 tabchar 仅在 js和html 时起作用
 */
export type FormatOption = { method: Method; step: string; preserveComments: boolean, tabsize: number, tabchar: string };

/**
 * 文本格式化
 * @param text
 * @param options option 参数
 */
export function txtFormat(text: string, options: FormatOption): string;
